double a, b, c;double t(){if ((a * c) / 2 != 0){double d = (a * c) / 2;return (d);}else return 0;}double c2(){if ((a * c) / 2 != 0){double d = 3.14159 * c * c;return (d);}else return 0;}double t2(){if ((a * c) / 2 != 0){double d = (a + b) * c / 2;return (d);}else return 0;}double q(){if ((a * c) / 2 != 0){double d = b * b;return (d);}else return 0;}double r(){if ((a * c) / 2 != 0){double d = a * b;return (d);}else return 0;}int main(){scanf("%lf %lf %lf", &a, &b, &c);putchar('T');putchar('R');putchar('I');putchar('A');putchar('N');putchar('G');putchar('U');putchar('L');putchar('O');putchar(':');putchar(' ');printf("%.3f", t());putchar('\n');putchar('C');putchar('I');putchar('R');putchar('C');putchar('U');putchar('L');putchar('O');putchar(':');putchar(' ');printf("%.3f", c2());putchar('\n');putchar('T');putchar('R');putchar('A');putchar('P');putchar('E');putchar('Z');putchar('I');putchar('O');putchar(':');putchar(' ');printf("%.3f", t2());putchar('\n');putchar('Q');putchar('U');putchar('A');putchar('D');putchar('R');putchar('A');putchar('D');putchar('O');putchar(':');putchar(' ');printf("%.3f", q());putchar('\n');putchar('R');putchar('E');putchar('T');putchar('A');putchar('N');putchar('G');putchar('U');putchar('L');putchar('O');putchar(':');putchar(' ');printf("%.3f", r());putchar('\n');return 0;}
